import {useState} from "react";
import {useNavigate} from "react-router-dom";
import {toast} from 'react-hot-toast';
import axios from "axios";
import "./login.css";
import {User} from "../../model/user.model";
import {LoginType} from "../../model/login.model";
import {useForm} from "react-hook-form";
import {getUriUser} from "../../UrlTools";

function Login  ()   {
    const navigate = useNavigate()
    const [data, setData] = useState({
        username: '',
        password: '',
    });
    const [userForm, setForm] = useState<LoginType>({
        username: "",
        password: "",
    });

    const {handleSubmit} = useForm();

    function onSubmit(data: any): any{
        axios.post(getUriUser(`login`), userForm).then((response) => {
            console.log(response);
            if (response.status===0){
                toast.success("Vous êtes connecté!");
                navigate('/');
            }else {
                toast.error("Une erreur s'est produite lors de la connexion");
            }
        })
    }

    /*const loginUser = async () => {
        //e.preventDefault()
        const {username, password} = data
        try {
            const {data} = await axios.post('/login', {
                username,
                password
            });
            if(data.error) {
                toast.error(data.error)
            } else {
                //setData({});
                navigate('/')
                window.location.reload();
            }
        } catch (error) {

        }
    }*/
    const registerUser = () => {
        //setData({});
        navigate('/register')
    }

    return (
        <div>
            {/* TopBar Component */}
            <div className="login">
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="lContainer">
                        <input
                            type="text"
                            placeholder="Email"
                            id="username"
                            value={userForm.username}
                            name={"username"}
                            onChange={(e: any) => {
                                setForm(prevState => ({
                                    ...prevState,
                                    username: e.target.value,
                                }));
                            }}
                            className="lInput"
                        />
                        <input
                            type="password"
                            placeholder="Password"
                            id="password"
                            value={userForm.password}
                            name={"password"}
                            onChange={(e: any) => {
                                setForm(prevState => ({
                                    ...prevState,
                                    password: e.target.value,
                                }));
                            }}
                            className="lInput"
                        />
                        <button onClick={onSubmit} className="lButton">
                            Login
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}
export default Login
