import {useState} from "react";
import {useNavigate} from "react-router-dom";
import {toast} from 'react-hot-toast';
import axios from "axios";
import "./register.css";
import type {Role} from "../../model/role.model";
import {getUriUser} from "../../UrlTools";
import {User} from "../../model/user.model";
import {useForm} from "react-hook-form";

function Register  ()   {
    const navigate = useNavigate()
    const [data, setData] = useState({
        email: '',
        username: '',
        contact: '',
        password: '',
        roles: [],
    });
    const [userForm, setForm] = useState<User>({
        id: undefined,
        email: "",
        username: "",
        password: "",
        contact: "",
        roles: ["USER_ROLE"]
    });

    const {handleSubmit} = useForm();

    function onSubmit(data: any): any{
        console.log("userForm ===> " + userForm.email);
        axios.post(getUriUser(`enregistrer`, userForm)).then((response) => {
            if (response.status===0){
                toast.success("Enregistrement éffectuer avec succès");
                navigate('/login');
            }else {
                toast.error("Une erreur s'est produite lors de l'enregistrement");
            }
        });
    }

    /*const registerAdmin = async () => {
        //e.preventDefault()
        const {name, email, username, contact, password, roles} = data
        try {
            const {data} = await axios.post('/enregister', {
                name,
                email,
                username,
                contact,
                password,
                roles,
            });
            setData({...data, roles: [{"id": 0, "version": 0, "name": "ROLE_USER"}]});
            if(data.error) {
                toast.error(data.error)
            } else {
                //setData({});
                //navigate('/admin')
                axios.post(getUriUser(`enregistrer`, userForm)).then((response) => {
                    if (response.status===0){
                        toast.success("Enregistrement éffectuer avec succès");
                        navigate('/login');
                    }else {
                        toast.error("Une erreur s'est produite lors de l'enregistrement");
                    }
                });
            }
        } catch (error) {

        }
    }*/

    const loginAdmin = () => {
        //setData({});
        navigate('/admin');
    }

    return (
        <div>
            {/* TopBar Component */}
            <div className="login">
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="lContainer">
                                <input
                                type="text"
                                placeholder="Email"
                                value={userForm.email}
                                name={"email"}
                               onChange={(e: any) => {
                                   setForm(prevState => ({
                                       ...prevState,
                                       email: e.target.value,
                                   }));
                               }}
                                className="lInput"
                                />
                                <input
                                type="text"
                                placeholder="Username"
                                value={userForm.username}
                                name={"username"}
                                onChange={(e: any) => {
                                    setForm(prevState => ({
                                        ...prevState,
                                        username: e.target.value,
                                    }));
                                }}
                                className="lInput"
                                />
                                <input
                                type="text"
                                placeholder="Contact"
                                value={userForm.contact}
                                name={"contact"}
                                onChange={(e: any) => {
                                    setForm(prevState => ({
                                        ...prevState,
                                        contact: e.target.value,
                                    }));
                                }}
                                className="lInput"
                                />
                                <input
                                type="password"
                                placeholder="Password"
                                value={userForm.password}
                                name={"password"}
                                onChange={(e: any) => {
                                    setForm(prevState => ({
                                        ...prevState,
                                        password: e.target.value,
                                    }));
                                }}
                                className="lInput"
                                />
                                <button className="lButton" intent={"success"}>
                                    Register
                                </button>
                                <button onClick={loginAdmin} className="lButton">
                                    Login
                                </button>
                    </div>
                </form>
            </div>
        </div>
    );
}
export default Register
